package maketsAllRequest.singleUser;import maketsAllRequest.Specification;import maketsAllRequest.EndPoints;import io.restassured.RestAssured;import io.restassured.http.ContentType;import io.restassured.specification.RequestSpecification;import org.junit.jupiter.api.*;import org.junit.jupiter.api.Test;public class SingleUserTests {    static RequestSpecification requestSpecification;    EndPoints endPoints = new EndPoints();    private static final String BASE_URL_POS = "https://reqres.in/";    @BeforeAll    static void setUp() {        requestSpecification = RestAssured.given()                .baseUri(BASE_URL_POS)                .accept(ContentType.JSON);    }   @Test   public void baseGetRequest(){       Specification.installSpecification(Specification.requestSpecification(BASE_URL_POS), Specification.responseSpecificationIndividualStatusCode(200));       SingleUserDataResponse  singleUserDataResponse = RestAssured.given(requestSpecification)                .when()                .get(endPoints.getSINGLE_USER())                .then().log().all()                .extract().as(SingleUserDataResponse.class);    }}