package maketsAllRequest.singleUser.dataClasses;public class UserDataFields {    public UserDataFields(){}    private Integer id;    private String email;    private String first_name;    private String last_name;    private String avatar;    public String getAvatar() {        return avatar;    }    public String getEmail() {        return email;    }    public String getFirst_name() {        return first_name;    }    public Integer getId() {        return id;    }    public String getLast_name() {        return last_name;    }    public UserDataFields(String avatar, String email, String first_name, Integer id, String last_name) {        this.avatar = avatar;        this.email = email;        this.first_name = first_name;        this.id = id;        this.last_name = last_name;    }}