package qwert.register.successRegister;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import qwert.Specification;import static io.restassured.RestAssured.given;public class TestSuccessRegistrationPostField {    private final static String url = "https://reqres.in/";    @Test    public void successRegisterTest(){        Specification.installSpecification(Specification.requestSpecification(url), Specification.responseSpecificationIndividualStatusCode(200));        Integer id = 4;        String token = "QpwL5tke4Pnpja7X4";        SuccessRegistrationPostField userEve = new SuccessRegistrationPostField("eve.holt@reqres.in", "pistol");        SuccessRegistrationResponseFields successReg = given()                .body(userEve)                .when()                .post("/api/register")                .then().log().all()                .extract().as(SuccessRegistrationResponseFields.class);        Assertions.assertNotNull(id);        Assertions.assertNotNull(token);        Assertions.assertEquals(id, successReg.getId());        Assertions.assertEquals(token, successReg.getToken());    }}