package qwert.register.unsuccessRegister;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import qwert.Specification;import static io.restassured.RestAssured.given;public class TestUnSuccessRegistration {    private final static String url = "https://reqres.in/";    @Test    public void unSuccessTest(){        Specification.installSpecification(Specification.requestSpecification(url), Specification.responseSpecificationIndividualStatusCode(400));        String error = "Missing password";        UnsuccessRegistrationPostFields userWithoutEmail = new UnsuccessRegistrationPostFields("ydney@fife");        UnSuccessRegistrationResponseFields unsuccessReg = given()                .body(userWithoutEmail)                .when()                .post("/api/register")                .then().log().all()                .extract().as(UnSuccessRegistrationResponseFields.class);        Assertions.assertNotNull(error);        Assertions.assertEquals(error, unsuccessReg.getError());    }}